import React from "react";
import defaultImage from "../../asset/images/default-product-image.png"; // 기본 이미지 import
import heartIcon from "../../asset/icon/ic_heart.png";

function ItemCard({ item }) {
  const imageUrl =
    item.images && item.images.length > 0 ? item.images[0] : defaultImage;

  return (
    <div>
      <img src={imageUrl} alt={item.name} />
      <h3>{item.name}</h3>
      <p>₩{item.price.toLocaleString()}</p>
      <div>
        <img src={heartIcon} alt="하트" />
        <span>{item.favoriteCount}</span>
      </div>
    </div>
  );
}

export default ItemCard;
