import React from "react";
import ItemCard from "./ItemCard";

function PandaBestList({ items }) {
  const bestItems = items
    .sort((a, b) => {
      const aFavoriteCount = Number(a.favoriteCount);
      const bFavoriteCount = Number(b.favoriteCount);
      return bFavoriteCount - aFavoriteCount;
    })
    .slice(0, 4);

  return (
    <div className="bestList-container">
      <h2 className="category">베스트 상품</h2>
      <div className="bestList">
        {bestItems.map((item) => (
          <ItemCard key={item.id} item={item} />
        ))}
      </div>
    </div>
  );
}

export default PandaBestList;
