import React, { useEffect, useState } from "react";
import ItemCard from "./ItemCard";
import { getItems } from "../../api/ItemApi"; // API를 import

function PandaBestList() {
  const [bestItems, setBestItems] = useState([]);

  useEffect(() => {
    async function fetchItems() {
      const items = await getItems(); // API에서 데이터 가져오기
      // favoriteCount 기준으로 내림차순 정렬
      const sortedItems = items.sort(
        (a, b) => b.favoriteCount - a.favoriteCount
      );
      setBestItems(sortedItems.slice(0, 4)); // 상위 4개만 설정
    }

    fetchItems();
  }, []); // 컴포넌트가 마운트될 때만 실행

  return (
    <div className="bestList-container">
      <h2 className="category">베스트 상품</h2>
      <div className="bestList">
        {bestItems.map((item) => (
          <ItemCard key={item.id} item={item} />
        ))}
      </div>
    </div>
  );
}

export default PandaBestList;
