import React, { useEffect, useState } from "react";
import ItemCard from "./ItemCard";
import { getItems } from "../../api/ItemApi";

function PandaBestList() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    getItems().then((data) => {
      const sortedItems = data
        .sort((a, b) => {
          const aFavoriteCount = Number(a.favoriteCount);
          const bFavoriteCount = Number(b.favoriteCount);
          return aFavoriteCount - bFavoriteCount;
        })
        .slice(0, 4);
      setItems(sortedItems);
    });
  }, []);

  return (
    <div className="bestList-container">
      <h2 className="category">베스트 상품</h2>
      <div className="bestList">
        {items.map((item) => (
          <ItemCard key={item.id} item={item} />
        ))}
      </div>
    </div>
  );
}

export default PandaBestList;
